import React, { useEffect, useRef, useState } from 'react'
import FountainAnimator from './fountain'
import skin1 from './img/coin1.svg'
import skin2 from './img/coin2.svg'

const App = () => {
  const [imgsLoaded, setImgsLoaded] = useState(false)
  const fountainCanvasRef = useRef(null)
  const img1 = useRef(null)
  const img2 = useRef(null)

  useEffect(() => {
    const loadImage = (image: any) => {
      return new Promise((resolve, reject) => {
        const loadImg = new Image()
        loadImg.src = image
        loadImg.onload = () => resolve(loadImg)
        loadImg.onerror = (err) => reject(err)
      })
    }

    Promise.all([skin1, skin2].map((image) => loadImage(image))).then(
      (arr) => {
        setImgsLoaded(true)
        console.log('Картинки загрузились')
        console.log(arr)

        const fountainAnimator = new FountainAnimator({
          animationSpeed: 2.9,
          power: 155,
          itemsNumber: 52,
          spreadAngle: 99,
          gravity: 8,
          minItemsSize: 215,
          maxItemsSize: 421,
          rotationSpeed: 0.7,
          imgSkins: arr,
          canvasRef: fountainCanvasRef,
        })
        fountainAnimator.start()
      },
      (err) => console.log(err),
    )

  }, [])

  return (
    <>
      <canvas ref={fountainCanvasRef} />
      <div className="hidden">
        <img src={skin1} ref={img1} />
        <img src={skin2} ref={img2} />
      </div>
    </>
  )
}

export default App
