import React, { useEffect, useRef } from 'react'
import FountainAnimator from './fountain'
import skin1 from './img/coin1.svg'
import skin2 from './img/coin2.svg'

const App = () => {
  const fountainCanvasRef = useRef(null)
  const img1 = useRef(null)
  const img2 = useRef(null)
  useEffect(() => {

    const loadImage = (image: any) => {
      return new Promise((resolve, reject) => {
        const loadImg = new Image()
        loadImg.src = image.url
        // wait 2 seconds to simulate loading time
        loadImg.onload = () =>
          setTimeout(() => {
            resolve(image.url)
          }, 2000)

        loadImg.onerror = err => reject(err)
      })
    }


    //document.addEventListener('load', () => {
    const fountainAnimator = new FountainAnimator({
      animationSpeed: 2.9,
      power: 155,
      itemsNumber: 52,
      spreadAngle: 99,
      gravity: 8,
      minItemsSize: 215,
      maxItemsSize: 421,
      rotationSpeed: 0.7,
      imgSkins: [img1, img2],
      canvasRef: fountainCanvasRef,
    })
    fountainAnimator.start()
    //})
  }, [])

  return (
    <>
      <canvas ref={fountainCanvasRef} />
      <div className="hidden">
        <img src={skin1} ref={img1} />
        <img src={skin2} ref={img2} />
      </div>
    </>
  )
}

export default App
