class FountainItem {
  constructor(canvasRef, shotAngle, width, id) {
    this.t = 0
    this.shotAngle = shotAngle
    this.width = width
    this.x = canvasRef.width - width / 2
    this.y = canvasRef.height
    this.id = id
  }

  incrementTime() {
    this.t += (animationSpeed * period) / 1000
  }

  move() {
    this.incrementTime()
    this.x = x_0 + power * this.t * Math.cos(this.shotAngle) - this.width / 2
    this.y =
      y_0 -
      power * this.t * Math.sin(this.shotAngle) +
      (g * this.t * this.t) / 2
    if (
      this.y > y_0 + 2 * this.width ||
      this.y < -2 * this.width ||
      this.x > canvasRef.width + 2 * this.width ||
      this.x < -2 * this.width
    ) {
      this.resetParams()
    }
  }

  resetParams() {
    this.t = 0
    this.shotAngle = randomFromInterval(dispersion())
    this.width = randomFromInterval(itemsSize)
    this.x = canvasRef.width - this.width / 2
    this.y = canvasRef.height
  }

  render() {
    context.save()
    context.translate(this.x + this.width / 2, this.y + this.width / 2)
    context.rotate(this.t * rotationSpeed * (2 * (this.id % 2) - 1))
    context.translate(-this.x - this.width / 2, -this.y - this.width / 2)
    context.drawImage(
      imgSkins[this.id % imgSkins.length],
      this.x,
      this.y,
      this.width,
      this.width,
    )
    context.restore()
    this.move()
  }
}

class FountainAnimator {
  constructor(props) {
    this.animationSpeed = props.animationSpeed
    this.power = props.power
    this.itemsNumber = props.itemsNumber
    this.spreadAngle = props.spreadAngle
    this.itemsSize = [props.minItemsSize, props.maxItemsSize]
    this.g = props.gravity
    this.rotationSpeed = props.rotationSpeed
    this.canvas = props.canvasRef
    this.imgSkins = props.imgSkinsRefs
  }

  setCanvasSize() {
    this.canvas.current.width = window.innerWidth
    this.canvas.current.height = window.innerHeight
    this.x_0 = canvas.width / 2
    this.y_0 = canvas.height
  }

  start() {
    this.setCanvasSize()
