import React, { useEffect, useRef } from 'react'
import FountainAnimator from './fountain'
import skin1 from './img/coin1.svg';
import skin2 from './img/coin1.svg';

const App = () => {
  const fountainCanvasRef = useRef(null)
  useEffect(() => {
    const fountainAnimator = new FountainAnimator({
      animationSpeed: 2.9,
      power: 155,
      itemsNumber: 52,
      spreadAngle: 99,
      gravity: 8,
      minItemsSize: 215,
      maxItemsSize: 421,
      rotationSpeed: 0.7,
      imgSkins: ['./img/coin1.svg', './img/coin2.svg'],
      canvasRef: fountainCanvasRef,
    })
    fountainAnimator.start()
  }, [])

  return <>
  <canvas ref={fountainCanvasRef} />
  <img src="" alt="" />
  </>
}

export default App
