class FountainAnimator {
  constructor() {
/*
    this.animationSpeed = props.animationSpeed
    this.power = props.power
    this.itemsNumber = props.itemsNumber
    this.spreadAngle = props.spreadAngle
    this.itemsSize = props.itemsSize
    this.g = props.g
    this.rotationSpeed = props.rotationSpeed
    this.rootEl = props.rootEl
    this.imgSkins = props.imgSkins*/
  }

  start() {
    const canvas = document.createElement('canvas')
    const context = canvas.getContext('2d')
    this.canvas = canvas
    this.context = context
    this.rootEl.appendChild(canvas)
  }
}

export default FountainAnimator
