const randomFromInterval = (interval) =>
  Math.random() * (interval[1] - interval[0]) + interval[0]

const dispersion = () => [
  (Math.PI * (180 - spreadAngle)) / 360,
  (Math.PI * (180 + spreadAngle)) / 360,
]

class FountainItem {
  constructor(props) {
    this.animationSpeed = props.animationSpeed
    this.power = props.power
    this.g = props.gravity
    this.rotationSpeed = props.rotationSpeed




    this.canvas = props.canvasRef.current
    this.context = this.canvas.getContext('2d')
    this.id = props.id
    this.skin = props.skin

  }

  incrementTime() {
    this.t += (animationSpeed * period) / 1000
  }

  move() {
    this.incrementTime()
    this.x = x_0 + power * this.t * Math.cos(this.shotAngle) - this.width / 2
    this.y =
      y_0 -
      power * this.t * Math.sin(this.shotAngle) +
      (g * this.t * this.t) / 2
    if (
      this.y > y_0 + 2 * this.width ||
      this.y < -2 * this.width ||
      this.x > this.canvas.width + 2 * this.width ||
      this.x < -2 * this.width
    ) {
      this.resetParams()
    }
  }

  setParams() {
    this.t = 0
    this.shotAngle = randomFromInterval(dispersion())
    this.width = randomFromInterval(itemsSize)
    this.x = this.canvas.width - this.width / 2
    this.y = this.canvas.height
  }

  render() {
    this.context.save()
    this.context.translate(this.x + this.width / 2, this.y + this.width / 2)
    this.context.rotate(this.t * rotationSpeed * (2 * (this.id % 2) - 1))
    this.context.translate(-this.x - this.width / 2, -this.y - this.width / 2)
    this.context.drawImage(skin, this.x, this.y, this.width, this.width)
    this.context.restore()
    this.move()
  }
}

class FountainAnimator {
  constructor(props) {
    this.animationSpeed = props.animationSpeed
    this.power = props.power
    this.itemsNumber = props.itemsNumber
    this.spreadAngle = props.spreadAngle
    this.itemsSize = [props.minItemsSize, props.maxItemsSize]
    this.g = props.gravity
    this.rotationSpeed = props.rotationSpeed
    this.canvas = props.canvasRef
    this.imgSkins = props.imgSkinsRefs
    //this.setCanvasSize()
